  .refresh--bg {
    position: absolute;
    top: 0; left: 0;
    z-index: var(--zmin);
    border-radius: 50%;
    width: 100%; height: 100%;
    background: #92ff00;
    filter: blur(20px);
    @include breakpoint(md)  { filter: blur(32px); }
  }

  .squiggle {
    position: relative;
    transform: translateY(-.24rem);
    @include breakpoint(md) { transform: translateY(-.8rem); }
  }

</style>


<script>
  import { provisions } from '../static/provisions'
  export default {
    data() {
      return {
        provisions,
        provision: '',
        shuffledNumbers: null
      }
    },
    methods: {
      randoProv : function(e) {
        this.provision = this.provisions[~~(Math.random() * this.provisions.length)]
      },
      // fisher: function() {
      //    this.shuffled = this.provisions
      //     .map((a) => ({sort: Math.random(), value: a}))
      //     .sort((a, b) => a.sort - b.sort)
      //     .map((a) => a.value)
      // }

      shuffle() {
        let numbers = [...this.provisions];
        let first,
          second,
          temp,
          count = numbers.length;
        for (let i = 0; i < 10; i++) {
          first = Math.floor(Math.random() * count);
          second = Math.floor(Math.random() * count);
          temp = numbers[first];
          numbers[first] = numbers[second];
          numbers[second] = temp;
        }
        this.shuffledNumbers = numbers.slice(0, 3);
      }
    },

    mounted() {
      this.shuffle()
    },

    // computed: {
    //   filteredprovisions: function() {
    //     return this.provisions.slice(0, 4).map(function () {
    //       return this.splice(Math.floor(Math.random() * this.length), 1)[0]
    //     },  provisions.slice())
    //   }
    // },

    created() {
      this.randoProv()
      // this.fisher()
      // console.log(this.shuffled)

    }
  }
</script>
